import React, { Component } from 'react';
import axios from 'axios';
import './nav-bar.css';
import {dataService} from '../services/search'
export default class NavBAr extends Component {


    state = {
      valueToSearch: ''
    };

    submitForm = () => {
      dataService.setData(this.state.valueToSearch);
    }
   
    render() {

        
        return (
        <nav class="navbar">
        <div class="container-fluid">
        <div class="navbar-header">
         
                <a class="navbar-brand" href="#" to="/models"><span class="fa fa-home"></span><span class="link"> Models</span></a>
          </div>
        <div class="navbar-brand " id="collapse-1">
             

              <form class="navbar-form navbar-right">
                          <div class="form-group">
                              <input type="text" class="form-control" placeholder="Search" value={this.state.valueToSearch}  onChange={e => {
            this.setState({
              valueToSearch: e.target.value
            },
            this.submitForm());
          }}
          
          ></input>
                          </div>
                          <button type="submit" class="btn"><span class="fa fa-search"></span></button>
                    </form>

        </div>
      </div>
    </nav> 
    
    );

    }
}
